insert into `res` (`hotel_group_id`, `hotel_id`, `sn`, `NAME`, `descript`, `res_body`, `res_head`, `folder_id`, `create_user`, `create_datetime`, `modify_user`, `modify_datetime`) values('1','-1','6f3959c1e0194060be1a6266e43e79ea','实时情况查询-预订查询-表格1','实时情况查询-预订查询-表格1','SELECT ta.hotel_group_id,ta.hotel_id,tb.descript,SUM(ta.rmnum)  rmnum FROM (\rSELECT r.hotel_group_id,r.hotel_id,IFNULL(SUM(r.rmnum),0) rmnum\rFROM rsv_src r,master_base r1\rWHERE r.hotel_group_id=r1.hotel_group_id\r  AND r.hotel_id=r1.hotel_id\r  AND r1.hotel_group_id = :hotel_group_id\r  AND r.hotel_group_id = :hotel_group_id\r  AND r1.id = r.accnt\r  AND (r.occ_flag = \'RF\' OR r.occ_flag = \'RG\')\r AND r1.id = r1.master_id AND r1.sta = \'R\' \rAND r.dep_date > :biz_date AND r.arr_date <= :biz_date\rGROUP BY  r.hotel_group_id, r.hotel_id  \r\rUNION ALL\r\rSELECT r.hotel_group_id,r.hotel_id,IFNULL(SUM(r.rmnum),0) rmnum\r    FROM rsv_src r,master_base r1\r      WHERE r.hotel_group_id=r1.hotel_group_id\r  AND r.hotel_id=r1.hotel_id\r  AND (r.occ_flag = \'MF\' OR r.occ_flag = \'MG\')\r        AND r.hotel_group_id = :hotel_group_id  AND r1.id = r.accnt\r        AND r1.hotel_group_id = :hotel_group_id  AND r1.sta = \'I\'\r        AND r1.rsv_class <> \'H\' AND r.dep_date > :biz_date AND r.arr_date <=:biz_date AND r1.id = r1.master_id\r  GROUP BY  r.hotel_group_id, r.hotel_id        \r ) AS ta, hotel tb  WHERE ta.hotel_group_id=tb.hotel_group_id AND ta.hotel_id=tb.id  GROUP BY ta.hotel_id,ta.hotel_id\r','<field><filedname>hotel_group_id</filedname><label>集团ID</label><dataType>字符</dataType><colIdx>0</colIdx><noShow>T</noShow></field><field><filedname>hotel_id</filedname><label>酒店ID</label><dataType>字符</dataType><colIdx>1</colIdx><noShow>T</noShow></field><field><filedname>descript</filedname><label>酒店</label><dataType>字符</dataType><colIdx>2</colIdx><noShow>F</noShow></field><field><filedname>rmnum</filedname><label>预计占用房数</label><dataType>数值</dataType><colIdx>3</colIdx><noShow>F</noShow></field>','0','ADMIN'