insert into `res` (`hotel_group_id`, `hotel_id`, `sn`, `NAME`, `descript`, `res_body`, `res_head`, `folder_id`, `create_user`, `create_datetime`, `modify_user`, `modify_datetime`) values('1','-1','4dab6f50f1ec43f4ae6f3ce125d8f7b5','实时情况查询-预订查询-表2','实时情况查询-预订查询-表2','SELECT ta.rmtype,\r\n       ta.descript,\r\n       SUM(ta.rmnum) rmsold,\r\n       ta.quantity  rmall,\r\n       (ta.quantity - SUM(ta.rmnum)) AS rmdiff,\r\n       ROUND(SUM(ta.rmnum)/ta.quantity*100,2)  AS rmper \r\n       FROM\r\n(SELECT r.rmtype,r3.descript,IFNULL(SUM(r.rmnum),0) rmnum,r3.quantity\r\nFROM rsv_src r,master_base r1,room_type r3\r\nWHERE (r.occ_flag = \'RF\' OR r.occ_flag = \'RG\')\r\n    AND r.hotel_group_id = :hotel_group_id AND r.hotel_id = :hotel_id AND r1.id = r.accnt\r\n    AND r1.id = r1.master_id AND r1.sta = \'R\' AND r1.hotel_group_id = :hotel_group_id\r\n    AND r1.hotel_id = :hotel_id AND r.rmtype = r3.code AND r3.hotel_group_id = :hotel_group_id AND r3.hotel_id =:hotel_id\r\n    AND r.dep_date > :biz_date AND r.arr_date <= :biz_date\r\nGROUP BY r.rmtype\r\nUNION ALL\r\nSELECT r.rmtype,r3.descript,IFNULL(SUM(r.rmnum),0) rmnum,r3.quantity\r\n    FROM rsv_src r,master_base r1,room_type r3\r\n      WHERE (r.occ_flag = \'MF\' OR r.occ_flag = \'MG\')\r\n        AND r.hotel_group_id = :hotel_group_id AND r.hotel_id = :hotel_id AND r1.id = r.accnt\r\n        AND r1.hotel_group_id = :hotel_group_id AND r1.hotel_id =:hotel_id AND r1.sta = \'I\'\r\n        AND r1.rsv_class <> \'H\' AND r.dep_date > :biz_date AND r.arr_date <=:biz_date \r\n        AND r3.hotel_group_id = :hotel_group_id AND r3.hotel_id = :hotel_id AND r3.code = r.rmtype AND r1.id = r1.master_id\r\n        GROUP BY r.rmtype\r\n) AS ta GROUP BY ta.rmtype\r\n','<field><filedname>rmtype</filedname><label>房类</label><dataType>字符</dataType><colIdx>0</colIdx><noShow>T</noShow></field>\r\n<field><filedname>descript</filedname><label>房类描述</label><dataType>字符</dataType><colIdx>1</colIdx><noShow>F</noShow></field>\r\n<field><filedname>rmall</filedname><label>总房数</label><dataType>数值</dataType><colIdx>2</colIdx><noShow>F</noShow></field>\r\n<field><filedname>rmsold</filedname><label>预计占用房数</label><dataType>数值</dataType><colIdx>2</colIdx><noShow>F</noShow></field>\r\n<field><filedname>rmdiff</filedname><label>可卖房数</label><dataType>数值</dataType><colIdx>2</colIdx><noShow>F</noShow></field>\r\n<field><filedname>rmper</filedname><label>占用率（%）</label><dataType>数值</dataType><colIdx>2</colIdx><noShow>F</noShow></field>','0','ADMIN','2014-08-23 11:15:44','ADMIN','2014-08-23 11:15:44');
